INSERT INTO `ietcv2__ovw_skills` (`id_skill`, `id_hero`, `name_skill`, `img_skill`, `description_skill`, `ulti`, `weapon`, `skill`, `passif`) VALUES
(5, 1, 'Fusil biotique', 'efe0ebb135e87dc26b60f0d20500dcd7553ad121ab2b10cd4ffb5db17be9c977.png', 'Le fusil d\'Ana tire des fléchettes qui peuvent restaurer la santé de ses alliés ou infliger des dégâts continus à ses ennemis. Elle peut utiliser la lunette du fusil pour zoomer sur les cibles et faire des tirs très précis.', 0, 1, 0, 0),
(6, 1, 'Fléchette hypodermique', '20707fd82265412fdc6d2353daa88ec7558cd71c89aa3ac6cf0e78bbbfcabd80.png', 'Ana tire une fléchette avec son arme de poing, rendant un ennemi inconscient (bien que tout dommage le réveille).', 0, 0, 1, 0),
(7, 1, 'Grenade biotique', 'c8190b234bf0a0e28eecffe162d0c942e6b8656e95f4688c6ca3b025fa5a487d.png', 'Ana lance une bombe biotique qui inflige des dégâts aux ennemis et soigne les alliés dans une petite zone d\'effet. Les alliés affectés reçoivent brièvement des soins accrus de toutes les sources, tandis que les ennemis pris dans l\'explosion ne peuvent pas être soignés pendant quelques instants.', 0, 0, 1, 0),
(8, 1, 'Nanoboost', '6fda18b343f3fd0e8dc50fa5a91589e1ca9ed7471a354f61dfc9f22b27b19497.png', 'Après qu\'Ana ait touché l\'un de ses alliés avec un Nanoboost, ils infligent plus de dégâts et subissent moins de dégâts des attaques des ennemis.', 1, 0, 0, 0),
(15, 2, 'Miséricorde', '9d3118f2f0a262678fb1b11ffbaa3f5e929e453cb4d7c819e69ff137cde918e1.png', 'Ange tire une balle avec son arme de poing automatique. Il est préférable de le réserver pour la défense personnelle d\'urgence.', 0, 1, 0, 0),
(26, 2, 'Ascendance angélique', 'd53b51360a83f35c450211d4a38859c839ad5720379d2e275a4f5826f3fe9967.png', 'Propulsée par sa combinaison Valkyrie, Ange ralentit la vitesse de sa descente depuis les hauteurs.', 0, 0, 0, 1),
(29, 2, 'Valkyrie', '0f5455a8a464e85370365bc954dc6c1cca11f4f5c09e7e6eee7a3dde29170c9e.png', 'Vous confère la capacité de voler. Améliore vos capacités.', 1, 0, 0, 0),
(27, 2, 'Résurrection', 'd26768cf459611eedc4fdcc9096ad3b196e8b2babb0782cf24d56f108325e3bb.png', 'Ange ramène un allié mort dans le combat en pleine santé.', 0, 0, 1, 0),
(28, 2, 'Ange gardien', 'e6d6f1f8288bb9a61fcfb85db90a49d3228277d18ac190898567bed4e7799ddc.png', 'Ange vole vers un allié ciblé, lui permettant de les atteindre rapidement et de fournir une assistance dans les moments cruciaux. En vol, sauter vous projette en avant, et s’accroupir vous projette en l’air.', 0, 0, 1, 0),
(33, 3, 'Dynamite', 'a326b202a821ab3e196d1faab6144460f470001b26a8b6f2ab96cb3af5f325b3.png', 'Ashe lance un explosif qui explose après un court délai ou immédiatement lorsqu\'il est tiré. L\'explosion de Dynamite allume également les ennemis en feu, infligeant des dégâts au fil du temps.', 0, 0, 1, 0),
(31, 3, 'Canon scié', 'e18f7b6e464f0cf34789babf7a76b7e1d851e62102dccfe10769bcf8716e5554.png', 'Ashe fait exploser les ennemis devant elle, les repoussant et se propulsant vers l\'arrière pour plus de mobilité.', 0, 0, 1, 0),
(32, 3, 'Le Cobra', 'cca8472a3e966de2b9985cee492e527eac5063e9881797dded4ca1e32e292bc0.png', 'Le fusil semi-automatique d\'Ashe tire des coups rapides au jugé. Ashe peut utiliser son viseur pour un tir plus précis et plus dommageable.', 0, 1, 0, 0),
(34, 3, 'B.O.B', 'e788b12d476e896dac3831bbc0842fb3b4d07a89b3c337959d9a6cd6ea5a7df5.png', 'Ashe invoque son acolyte omniacque de confiance, B.O.B., qui charge et renverse les ennemis dans les airs, puis déclenche des tirs de suppression à l\'aide de ses canons de bras.', 1, 0, 0, 0),
(35, 4, 'Exo-Bottes', '0d598afba5a03761d617d735d5435323c56108655b5017ddf8c36c4af36b9b73.png', 'En s\'accroupissant d\'abord, Baptiste peut sauter plus haut.', 0, 0, 0, 1),
(36, 4, 'Bio-Lanceur', 'af62dfb3476b6389472188ce9e6e0e296928a5cb3ae87a4ed5133c8330e46f0d.png', 'Le lanceur biotique de Baptiste, avec ses trois rafales, récompense la précision et le contrôle du recul en infligeant des dégâts considérables.', 0, 1, 0, 0),
(39, 4, 'Salve régénérante', 'd887d165f7b0eb98a30af8f274ff740c6c7735af719b93e95dd9da17558815d8.png', 'Baptiste active une explosion régénératrice intense qui se soigne lui-même et ses alliés proches au fil du temps.', 0, 0, 1, 0),
(38, 4, 'Tir secondaire du Bio-Lanceur', 'af62dfb3476b6389472188ce9e6e0e296928a5cb3ae87a4ed5133c8330e46f0d.png', 'Le lanceur biotique sert également de dispositif de guérison, lançant des projectiles qui soignent les alliés près du point d\'impact.', 0, 1, 0, 0),
(40, 4, 'Champ d\'immortalité', 'd66d82bab88fc813080dd15e31629cc3aa7c8b41cfb41d5b962b628dd345e433.png', 'Baptiste utilise un appareil pour créer un champ qui empêche les alliés de mourir. Le générateur peut être détruit.', 0, 0, 1, 0),
(41, 4, 'Matrice d\'amplificatrice', '0a31371b55e4007e67a86f8495949970d20f64b2f0166e78d3fd16217e090a83.png', 'Baptiste projette une matrice qui double les dégâts et les effets de soin des projectiles alliés.', 1, 0, 0, 0),
(42, 5, 'Configuration : Reconnaissance', 'a2a15d0b50a53e0477ab65e55ed46248cf86ac976e5cef7c64078470dfa59807.png', 'En mode Recon, Bastion est entièrement mobile, équipé d\'une mitraillette qui tire des rafales régulières de balles à moyenne portée.', 0, 1, 0, 0),
(43, 5, 'Configuration : Assaut', '5cf3fc40ec6eeee9716f66a934d9bf7f0803a924e72c12c5d1a6c74e57c933bd.png', 'En mode Assault, Bastion a des déplacements lents mais est équipé d\'un puissant canon rotatif. Le mode Assaut est temporaire.', 0, 1, 0, 0),
(44, 5, 'Reconfiguration', '56bef8912e83cac59dc0acb08bcaef591ff2ed52385424e8d753c03bbb77e4e9.png', 'Bastion se transforme entre les modes Reconnaissance et Assaut pour s\'adapter aux conditions du champ de bataille.\r\n', 0, 0, 1, 0),
(45, 5, 'Grenade tactique A-36', '22f9f8c043e1746011d7dcee2e811e0d24470bd0eb5128fca9c3aea2f2dcfe69.png', 'Tire une grenade qui rebondit sur les murs et explose lorsqu\'elle touche les ennemis ou le sol.', 0, 0, 1, 0),
(46, 5, 'Configuration : Artillerie', '06c377aef476bfb21ee1dca3d4d1151f8a4131b388a5489f3dfce6fb232a2711.png', 'Bastion deviens immobile et tire jusqu\'à trois puissants obus d\'artillerie.\r\n', 1, 0, 0, 0),
(47, 6, 'Batterie de Canons', '12339775bfc5bfb8a4ad735a2eef452992e90f5374d341baf09f19bc9968147a.png', 'Les canons d\'assaut automatiques de Bouldozer détruisent les menaces à moyenne portée.', 0, 1, 0, 0),
(48, 6, 'Griffe-Crampon', 'e81e6ba9fb3391f29528021e453a0d3c16da1fc5e378994e6d00783356e0f2c9.png', 'Projette une griffe-crampon pour se balancer rapidement depuis son point d’ancrage. Permet de provoquer des collisions à grande vitesse pour blesser et repousser les ennemis.', 0, 0, 1, 0),
(49, 6, 'Roulé-Boulé', 'b7f7e90bf3c3f26704133263896bded18e523e29dad2fe04c5f3c1bfa4472579.png', 'Se roule en boule et augmente sa vitesse de déplacement.', 0, 0, 1, 0),
(50, 6, 'Pillonage', '3e884373721448d109c1646599cfe651a5bccdbc7a12d5062372c91b28acd80c.png', 'Atterrit avec force, ce qui blesse les ennemis et les projette en l’air.', 0, 0, 1, 0),
(51, 6, 'Bouclier Dynamique', 'fa65af9c692405f968fafbeedcdfc9092b38a050dc3562bd41280390ccd7a21d.png', 'Bouldozer gagne des points de vie supplémentaires temporaires, dont le montant dépend du nombre d’adversaires à proximité.', 0, 0, 1, 0),
(52, 6, 'Champs de mines', '17a54a345b715bc4301b330d8eb394f57e1c66308a8cba67f426de76a71ba868.png', 'Bouldozer déploie un champ de mines de proximité longue durée qui explosent au contact des ennemis.', 1, 0, 0, 0),
(53, 7, 'Inspiration', '487545fb37163e4029e1e8483266180fbcc59b7e8482062563168998774615f8.png', 'Quand Brigitte inflige des dégâts aux ennemis cela soigne les alliés proches.', 0, 0, 0, 1),
(54, 7, 'Fléau à réaction', '1304eead9caa02eb2057c7f9a3eb758bd438086223a8d6c475bb6912da5b8132.png', 'Arme de mêlée à portée étendue.', 0, 1, 0, 0),
(55, 7, 'Module de réparation', '5f9f1e00af992f4a090cafc8e83821dd2848f22cb9e0205fafcedacb26bd7335.png', 'Soigne un allié pendant une courte durée.', 0, 0, 1, 0),
(56, 7, 'Fléau cinglant', '50af1a0bc61a1f022fe2ee69aa460f151aba3c0ccde41377bfb0d5c45b2c55a3.png', 'Brigitte lance son fléau vers l\'avant pour éloigner un ennemi d\'elle.', 0, 0, 1, 0),
(57, 7, 'Bouclier-écran', '2b1bb6338180161e55c5f2184879dce395eca9e58f974befe729293fcc3dfdca.png', 'Maintenez \"Tir Secondaire\" enfoncé pour déployer un bouclier d’énergie devant vous.', 0, 0, 1, 0),
(58, 7, 'Charge de bouclier', '79f145b07b35e744fd0400dceff5fb11a06f9a73c48809f1cfb462fe82fe1185.png', 'Quand le bouclier-écran est déployé, vous pouvez vous projeter vers l’avant pour repousser votre adversaire.', 0, 0, 1, 0),
(61, 8, 'Pacificateur', 'afb27d71eeb6f3635ac1ff236c7b3f9e3c6d017360ce022d5d384288ff95bce7.png', 'Revolver précis et puissant.', 0, 1, 0, 0),
(62, 8, 'Roulade', '24a3f2f619859812bba6b6374513fa971b6b19ccb34950c02118b41cc4f93142.png', 'Vous effectuez une roulade dans la direction que vous empruntez afin de réduire les dégâts que vous subissez et de recharger votre arme.', 0, 0, 1, 0),
(60, 7, 'Ralliement', '08552423da53faa55238bd66a2a72d32d1038e42f2eebc911921bc9d5d575432.png', 'Vous vous déplacez plus rapidement et octroyez des points de vie aux personnages alliés proches.', 1, 0, 0, 0),
(63, 8, 'Pacificateur(2nd)', 'afb27d71eeb6f3635ac1ff236c7b3f9e3c6d017360ce022d5d384288ff95bce7.png', 'Cassidy peut ventiler le marteau du Pacificateur pour décharger sauvagement les cartouches restantes du barillet.', 0, 0, 1, 0),
(64, 8, 'Grenade magnétique', '47a593ad7f83394b011161e67a6f414e49f2221c5e014ea909fdcd9df4f94371.png', 'Cassidy lance une grenade à courte portée qui poursuit les ennemis proches et inflige des dégâts supplémentaires si elle s’accroche à eux.', 0, 0, 1, 0),
(65, 8, 'Implacable', '346cb576a19f978a7b2c52859c5639423f076d66ca740658da0d46955ab2c97d.png', 'Faites face à vos ennemis. Appuyez pour les verrouiller, puis réappuyez pour tirer.', 1, 0, 0, 0),
(66, 9, 'Mauvais perdant', '82e123991088b3c13edb1158d0bc4281f752896f8f3ddb76d4e3407e8f52f88d.png', 'Vos explosifs ne vous blessent pas. Vous larguez des bombes à votre mort.', 0, 0, 0, 1),
(67, 9, 'Lance-grenades', 'e43d916b0851551d17a2385f92497cdcfcd781ac155663643a1e1ff3500bbf37.png', 'Le Lance-grenades de Chacal lance des grenades à une distance significative. Ils rebondissent pour atteindre leur destination et explosent lorsqu\'ils frappent un ennemi.', 0, 1, 0, 0),
(68, 9, 'Mine incapacitante', '1d6135a3089363cf2c59d5157740cf5dc4ad572bc215553672a79812c397dbd2.png', 'Après avoir placé l\'une de ses mines incapacitante, Chacal peut la déclencher pour endommager les ennemis et les envoyer voler ou se propulser dans les airs.', 0, 0, 1, 0),
(69, 9, 'Piège d\'acier', '425136ea32c08355f33df230d900a8cc8010ee7a2b27314f6a1e395bd78eb5aa.png', 'Chacal lance un piège géant à dents de métal. Si un ennemi s\'approche trop près du piège, il s\'accroche, se blesse et s\'immobilise.', 0, 0, 1, 0),
(70, 9, 'Pneumastic', 'd16b5c6c337b1c0ecee3e5bf0760c870927e921fed8f0caed12dcb996b5347ae.png', 'Chacal fait tourner une bombe pneumatique motorisée et l\'envoie rouler sur le champ de bataille, escaladant les murs et les obstacles. Il peut faire exploser à distance le Pneumastic pour infliger de graves dégâts aux ennemis pris dans l\'explosion.', 1, 0, 0, 0),
(71, 10, 'Déferrailleur', '50b6e388a6693ce31b5a1889e53037970a1b884b8265834f33dd6f7a3dedce44.png', 'Arme à dispersion de courte portée.', 0, 1, 0, 0),
(72, 10, 'Déferrailleur(2nd)', '50b6e388a6693ce31b5a1889e53037970a1b884b8265834f33dd6f7a3dedce44.png', 'En tir secondaire, Chopper peut lancer une boule de shrapnel qui explose plus loin, dispersant des fragments de métal du point d\'impact.', 0, 1, 0, 0),
(73, 10, 'Inhalateur', '9cb8d867671bf917a9ba5d4a28ab6cb6ceeb10de2813567fcad97aca32c58499.png', 'Soignez-vous et réduisez les dégâts subis sur une courte période.', 0, 0, 1, 0),
(74, 10, 'Traquelard', '139deabeb3d62c451b558367bf5e64641583355d99ed90fe42107a5716fdca3b.png', 'Chopper lance sa chaîne sur une cible, si il l\'attrape, il l\'attire à bout portant.', 0, 0, 1, 0),
(75, 10, 'Équarrisseur', '78faa90f268424c6e770168ec15f5bb880d0b14e5d10f22c30bfbb64ffe244a8.png', 'Endommagez et repoussez les ennemis devant vous.', 1, 0, 0, 0),
(76, 11, 'Éjection !', '692a603c9945e600b195d1fd56364979363926f42d950b95c4811e7047e94c97.png', 'Le Meka de D.Va l\'éjectera automatiquement lorsqu\'il atteindra 1 point de vie. Cela force D.Va à être sans Meka.', 0, 0, 0, 1),
(77, 11, 'Fusio-canons', '29bd15ebb32c23483a59df18a79f71301b2128c05b2f8d0c052ab86e0715e07d.png', 'Le Meka de D.Va est équipé de deux canons rotatifs à courte portée. Ils lancent des tirs continus à dégâts élevés sans avoir besoin de recharger, mais ralentissent les mouvements de D.Va pendant qu\'ils sont actifs.', 0, 1, 0, 0),
(78, 11, 'Pistolaser', '24074dececa8bf7b57628509e70731916db62b5ab5b30660ac1727b20bbc5b4d.png', 'Hors Meka, D.Va peut continuer le combat avec un blaster automatique.', 0, 1, 0, 0),
(79, 11, 'Matrice défensive', '2713a8ef6742f4009da072d66d382eb8ff3dd8e00828deadb5af1a93b0d8ce4b.png', 'Bloquez les projectiles dans une zone devant vous.', 0, 0, 1, 0),
(80, 11, 'Turboréacteurs', '89d6beb25b21eacd5a181b96438436692d9ad403fcef19873e506f6d46ed37a3.png', 'Volez dans la direction à laquelle vous faites face. Vous pouvez vous retourner et changer de direction ou foncer à travers ses ennemis, les repoussant.', 0, 0, 1, 0),
(81, 11, 'Micro-missiles', '1787326d46d04aa10872fc30fe512ab3575781735110dfb178ff38da352facb7.png', 'Lance une volée de roquettes explosives.', 0, 0, 1, 0),
(82, 11, 'Auto-destruction', 'f76e4b710ceb71f2e260029fc8d0ad15b227e3f006ab1b6c0c4e44da98c2d614.png', 'Vous vous éjectez du méca en déclenchant une surcharge qui le fait exploser après un court délai.', 1, 0, 0, 0),
(83, 11, 'Appel du Meka', '848d8cd24eb0327a023a564447d79dd8b367c21a378d328cd92896a21b775219.png', 'Appelez un nouveau Meka causant des dégâts a proximité.', 0, 0, 0, 1),
(84, 12, 'La meilleure défense...', 'Doomfist_ability_icon_4.png', 'Doomfist génère une sursanté temporaire lorsqu\'il inflige des dégâts de capacité.', 0, 0, 0, 1),
(85, 12, 'Calibre prosthétique', '49c404d5887c562cdb5aafb55979c5589a450c0e18fa9d62a3c8cc0ac585886e.png', 'Doomfist tire une rafale à courte portée des jointures de son poing. Ses munitions sont automatiquement régénérées en peu de temps.', 0, 1, 0, 0),
(86, 12, 'Onde de choc', '20f721d5fd05f91e203d6e0007a137bdc90a161356f85d5aa88b2ab02b8da22b.png', 'Sautez en avant et écrasez le sol.', 0, 0, 1, 0),
(87, 12, 'Parade puissante', '56267df764931eadeae09513e0f1b9594ae238eebba84878c501ba4b69acb71f.png', 'Vous vous protégez des attaques frontales. Bloquez un nombre important de dégâts pour renforcer Direct d’enfer.', 0, 0, 1, 0),
(88, 12, 'Direct d\'enfer', 'aa2a067fe151ac2795fe77546711c07be5f0880d9ae5dce554cab0c933d927f0.png', 'Après avoir chargé, Doomfist se précipite vers l\'avant et repousse un ennemi, infligeant des dégâts supplémentaires s\'il touche un mur.', 0, 0, 1, 0),
(89, 12, 'Frappe météore', 'fa96b2650987b3784a725a0fb1704f7191a686a4f42c9ccf61c404e2f08b158c.png', 'Doomfist bondit dans le ciel, puis s\'écrase au sol, infligeant des dégâts importants. Appuyez sauter dans les airs. Déplacez le cercle de ciblage, puis appuyez pour frapper la zone ciblée.', 1, 0, 0, 0),
(90, 13, 'Planeur', 'e90772df1f7e55d4ea6e639ce909773ae2e28172d06afd8afa00e85bbef0a20d.png', 'Maintenez le saut pour planer en tombant.', 0, 0, 0, 1),
(91, 13, 'Tir triangulaire', '8fc9f68f9cdcd7c90ce902d1882bca2e783609f7f0e62c8ee00996eec4026063.png', 'Écho tire trois balles à la fois, en motif triangulaire.', 0, 1, 0, 0),
(92, 13, 'Bombes collantes', 'dcaa626e367b80b5bcd2425e26e0075b5343990785bc96e1705325904f73fe96.png', 'Écho tire une volée de bombes collantes qui explosent après un certain délai.', 0, 0, 1, 0),
(93, 13, 'Vol', '711f211c988ff1fafe99276bddefa3a861789e4b27859260e8ad87df5b879337.png', 'Vous volez rapidement vers l’avant, puis librement pendant quelques instants.', 0, 0, 1, 0),
(94, 13, 'Rayon focalisé', '6552feeb85a32ae7131df50f61a9c0fa24bf347bf007e208a356a940561129ae.png', 'Vous canalisez un rayon pendant quelques secondes, qui inflige de lourds dégâts aux cibles ayant moins de la moitié de leurs points de vie.', 0, 0, 1, 0),
(95, 13, 'Duplication', '457d6ebcb2b92d1e7f7a7174b426903879e131422196825589adbc5e5d9f7efb.png', 'Écho duplique un héros ennemi ciblé et utilise ses capacités. Pendant la duplication, Écho charge sa capacité ultime plus vite. La cible d\'Echo ne peut pas changer de héros pendant la durée de la capacité.', 1, 0, 0, 0),
(96, 14, 'Baiser de la veuve', '13a1e7903c23a5e140aa01fa930f585c1ea69e1e24378c0462f42053085f7ad9.png', 'Arme d’assaut automatique. Maintenez pour une arme de tireur d\'élite à longue portée.', 0, 1, 0, 0),
(97, 14, 'Grappin', '72fec7acac37ad840835839e72f368134498583686e91f7e30fe5d48aa44f7a1.png', 'Vous lancez un grappin qui vous hisse sur un rebord.', 0, 0, 1, 0),
(98, 14, 'Mine venimeuse', '82bb61b02224901170d8dc2fae143e695649cdfd7f5173a0e63a7742fd45bd84.png', 'Fatale colle une mine de venin à armement rapide sur presque toutes les surfaces. Lorsqu\'une cible erre à portée du déclencheur de mouvement de la mine, elle explose, délivrant du gaz toxique à tous les ennemis à proximité et offrant un visuel des victimes à Fatale.', 0, 0, 1, 0),
(99, 14, 'Infravision', 'b7783e8f83afc45dac37b80d0801a5b8200727e3dde299510d2f1886e0c7237b.png', 'La visière de reconnaissance de Fatale lui permet de voir les signatures thermiques de ses cibles à travers les murs et les objets pendant une durée modérée. Cette vision améliorée est partagée avec ses alliés.', 1, 0, 0, 0),
(100, 15, 'La moisson', '21fb34e5a009987c73f686336cd61d4e4b771a8a2d9b6753e62f65b41e6b6c51.png', 'Faucheur vole la santé de ses ennemis en les endommageant.', 0, 0, 0, 1),
(101, 15, 'Pompes funèbres', '76b6866587871744aca310645f66d2d07755c5a55093eb8734ed0898b17efc51.png', 'Doubles armes à propagation à courte portée.', 0, 1, 0, 0),
(102, 15, 'Forme spectrale', 'dbcceb2a764dcf8bb808078f7a32a039fd259c72e557dcde3fb70b2041247c9a.png', 'Faucheur devient une ombre pendant une courte période de temps. Sous cette forme, il ne subit aucun dommage et est capable de traverser les ennemis, mais ne peut pas tirer avec ses armes ni utiliser d\'autres capacités.', 0, 0, 1, 0),
